   // Split the text into words
  const words = text.split(" ");

  // Iterate through the words and replace with synonyms if available
  const paraphrasedWords = words.map((word) => {
    const synonymsForWord = synonyms[word.toLowerCase()];
    return synonymsForWord ? synonymsForWord[Math.floor(Math.random() * synonymsForWord.length)] : word;
  });

  // Join the paraphrased words back into a sentence
  return paraphrasedWords.join(" ");
}

